var app = require('.')();

/*
$ curl -X POST --data '{"hurr": "durr"}' -H 'Content-Type: application/json' http://localhost:3000/test
{"success":true}

$ curl -X POST --data '{"hurr": "not durr"}' -H 'Content-Type: application/json' http://localhost:3000/test
{"error":"hurr is not durr"}
*/

app.post('/test', function(req, res) {
  if (req.body.hurr === 'durr')
    res.ok();
  else
    res.error('hurr is not durr');
});

app.listen(3000, function() {
  console.log('listening on port 3000');
});
